        private class SearchIndex : AbstractIndexCreationTask<Document, SearchIndex.Result>
        {
            public class Result
            {
                public string Beschreibung { get; set; }
            }

            public SearchIndex()
            {
                Map = documents => from document in documents
                                   select new
                                   {
                                       Beschreibung = document.Beschreibung
                                   };

                Analyze(x => x.Beschreibung, "Raven.Server.Documents.Indexes.Persistence.Lucene.Analyzers.Collation.Cultures.DeCollationAnalyzer, Raven.Server");
            }
        }

        private class Document
        {
            public string Id { get; set; }
            public string Beschreibung { get; set; }
        }
